const canvas=document.querySelector("#matrix-background"),ctx=canvas.getContext("2d");canvas.width=document.documentElement.clientWidth,canvas.height=document.documentElement.clientHeight;const columns=canvas.width/20,drops=[];for(let i=0;i<columns;i++)drops[i]=1;function draw(){ctx.fillStyle="rgba(0, 0, 0, 0.05)",ctx.fillRect(0,0,canvas.width,canvas.height),ctx.fillStyle="rgba(0, 213, 190, 0.3)",ctx.font="15px monospace";for(let t=0;t<drops.length;t++){let e=String.fromCharCode(128*Math.random());ctx.fillText(e,20*t,20*drops[t]),20*drops[t]>canvas.height&&Math.random()>.975&&(drops[t]=0),drops[t]++}}setInterval(draw,33);const targetDate=Date.UTC(2025, 3, 5, 2, 30);function calculateTimeLeft(){let t=targetDate-+new Date,e={};return t>0&&(e={days:Math.floor(t/864e5),hours:Math.floor(t/36e5%24),minutes:Math.floor(t/1e3/60%60),seconds:Math.floor(t/1e3%60)}),e}function updateCountdown(){let t=calculateTimeLeft();document.querySelector("#countdown-days").textContent=t.days,document.querySelector("#countdown-hours").textContent=t.hours,document.querySelector("#countdown-minutes").textContent=t.minutes,document.querySelector("#countdown-seconds").textContent=t.seconds}setInterval(updateCountdown,1e3);
